// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

contract TournamentFactory {
    address public owner;

    Tournament[] public tournaments;

    enum Status {
        Pending,
        OnGoing,
        Finished
    }

    struct Tournament {
        uint8 id;
        uint entryFee;
        uint8 participents;
        uint startDate;
        Status status;
    }

    constructor() {
        owner = msg.sender;
    }

    // error AdminOnly(address msg_sender);
    event TournamentCreated(
        uint8 id,
        uint entryFee,
        uint8 participents,
        uint startDate,
        Status status
    );

    function createTournament(
        uint8 _id,
        uint _entryFee,
        uint8 _participents,
        uint _startDate,
        Status _status
    ) public {
        // require(msg.sender == owner, AdminOnly(msg.sender));
        tournaments.push(
            Tournament({
                id: _id,
                entryFee: _entryFee,
                participents: _participents,
                startDate: _startDate,
                status: _status
            })
        );
        emit TournamentCreated(
            _id,
            _entryFee,
            _participents,
            _startDate,
            _status
        );
    }

    function getTournaments() public view returns (Tournament[] memory) {
        return (tournaments);
    }
}
